function lockScroll() { document.documentElement.style.overflow = 'hidden'; }
function unlockScroll() { document.documentElement.style.overflow = ''; }

function openModal() { 
  modal.classList.add("active"); 
  lockScroll(); 
}
function closeModal() { 
  modal.classList.remove("active"); 
  unlockScroll(); 
  currentCard = null;
}

// =================== Card Hover Effect ===================
document.querySelectorAll(".card").forEach(card => {
  card.addEventListener("mousemove", e => {
    const rect = card.getBoundingClientRect();
    const mx = ((e.clientX - rect.left) / rect.width) * 100;
    const my = ((e.clientY - rect.top) / rect.height) * 100;
    card.style.setProperty("--mx", mx + "%");
    card.style.setProperty("--my", my + "%");
  });
});

// =================== Modal Slider Singleton ===================
const ModalSlider = (() => {
  let body, track, prevBtn, nextBtn, dotsWrap, counter;
  let images = [], index = 0;

  function buildUI() {
    body.innerHTML = `
      <div class="fs-slider">
        <div class="fs-slider__track"></div>
        <button class="fs-slider__btn fs-slider__btn--prev" aria-label="Prev">â€¹</button>
        <button class="fs-slider__btn fs-slider__btn--next" aria-label="Next">â€º</button>
        <div class="fs-slider__dots"></div>
        <div class="fs-slider__counter" aria-live="polite"></div>
      </div>
    `;
    track = body.querySelector(".fs-slider__track");
    prevBtn = body.querySelector(".fs-slider__btn--prev");
    nextBtn = body.querySelector(".fs-slider__btn--next");
    dotsWrap = body.querySelector(".fs-slider__dots");
    counter = body.querySelector(".fs-slider__counter");

    track.innerHTML = images.map(src => `<div class="fs-slide"><img src="${src}" alt=""></div>`).join("");
    dotsWrap.innerHTML = images.map((_,i)=>`<button type="button" aria-label="Go to ${i+1}"></button>`).join("");

    dotsWrap.querySelectorAll("button").forEach((b,i)=>b.addEventListener("click",()=>go(i)));
    prevBtn.addEventListener("click", prev);
    nextBtn.addEventListener("click", next);
  }

  function update() {
    const w = body.querySelector(".fs-slider").clientWidth;
    track.style.transform = `translate3d(${-index*w}px,0,0)`;
    track.style.transition = "transform .35s ease";
    dotsWrap.querySelectorAll("button").forEach((b,i)=> b.setAttribute("aria-current", i===index?"true":"false"));
    counter.textContent = `${index+1} / ${images.length}`;
  }

  function open(srcList,start=0){
    images = srcList.slice();
    index = Math.max(0, Math.min(images.length-1,start));
    body = modalSlider;
    buildUI();
    update();
  }

  function next(){ index = (index+1)%images.length; update(); }
  function prev(){ index = (index-1+images.length)%images.length; update(); }
  function go(i){ index = Math.max(0,Math.min(images.length-1,i)); update(); }

  return { open, next, prev };
})();

// =================== Card Click ===================
let currentCard = null;
document.querySelectorAll(".card").forEach(card=>{
  card.addEventListener("click",()=>{
    currentCard = card;
    const images = JSON.parse(card.dataset.images||"[]");
    ModalSlider.open(images);

    textWrapper.style.display = "none";
    modalButtons.forEach(b=>b.classList.remove("active"));
    modalSlider.style.display = "block";

    openModal();
  });
});

// =================== Modal Buttons ===================
modalButtons.forEach(btn=>{
  btn.addEventListener("click",()=>{
    if(!currentCard) return;
    const target = btn.dataset.target;
    const projectId = currentCard.dataset.projectId;
    const contentObj = cardTextContent[projectId][target];
    const content = contentObj[currentLang] || "";

    textContainer.innerHTML = `<p>${content}</p>`;
    textWrapper.style.display = "block";
    modalSlider.style.display = "none";

    modalButtons.forEach(b=>b.classList.remove("active"));
    btn.classList.add("active");
  });
});

